using namespace std;

class Solution {
public:
    int findLength(vector<int>& A, vector<int>& B)
    {
        int m = A.size();
        int n = B.size();

        std::vector<std::vector<int>> dp(m, std::vector<int>(n, 0));

        for(int i = 0; i < m; ++i)
        {
            dp[i][0] = A[i] == B[0] ? 1 : 0;
        }

        for(int i = 0; i < n; ++i)
        {
            dp[0][i] = A[0] == B[i] ? 1 : 0;
        }

        int Max = 0;

        for(int i = 1; i < m; ++i)
        {
            for(int j = 1; j < n; ++j)
            {
                if(A[i] == B[j])
                {
                    dp[i][j]  = dp[i - 1][j - 1] + 1;
                }
                else
                {
                    dp[i][j] = 0;
                }

                Max = std::max(Max , dp[i][j]);
            }
        }

        for(int i = 0; i < m; ++i)
        {
            for(int j = 0; j < n; ++j)
            {
                std::cout<<dp[i][j]<< " ";
            }
            std::cout<<std::endl;
        }

        return Max;
    }
};

