#include "header.h"

class Solution {
public:
    vector<int> findSubstring(string s, vector<string>& words) {
        if(words.empty()) return {};
        int m = s.size(), n = words.size(), len = words[0].size();
        int cnt = 0;
        unordered_map<string, int> map;
        for(const auto& w : words) {
            ++map[w];
            ++cnt;
        }

        vector<int> res;
        for(int startIdx = 0; startIdx < len; ++startIdx) {
            int i = startIdx, j = i;
            while(i + n * len <= m) {
                const auto& t = s.substr(j, len); 
                if(!map.count(t)) {
                    while(i != j) {
                        ++map[s.substr(i, len)];
                        ++cnt;
                        i += len;
                    }
                    j += len;
                    i = j;
                    continue;
                }
                if(map[t] > 0) {
                    --map[t];
                    --cnt;
                    if(cnt == 0) res.push_back(i);
                    j += len;
                }
                else
                {
                    ++map[s.substr(i, len)];
                    i += len;
                    ++cnt;
                }
            }
        }
        return res;
    }
};

